package com.example.restaurateur.Reservation;import android.content.Context;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.example.restaurateur.Offer.OfferModel;import com.example.restaurateur.R;import com.example.restaurateur.UserInformationActivity;import java.util.ArrayList;import java.util.HashMap;public class ReservationsListAdapter extends RecyclerView.Adapter<ReservationsListAdapter.ReservationViewHolder> {    private ArrayList<ReservationModel> dataSet;    private LayoutInflater mInflater;    private Context context;    private HashMap<Integer, OfferModel> offersData;    public ReservationsListAdapter(Context context, ArrayList<ReservationModel> data, HashMap<Integer, OfferModel> offersData) {        this.dataSet = data;        this.mInflater = LayoutInflater.from(context);        this.context = context;        this.offersData = offersData;    }    @NonNull    @Override    public ReservationViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = mInflater.inflate(R.layout.reservation_cardview, parent, false);        view.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent myIntent = new Intent(context.getApplicationContext(), UserInformationActivity.class);                context.startActivity(myIntent);            }        });        ReservationViewHolder myViewHolder = new ReservationViewHolder(view);        return myViewHolder;    }    @Override    public void onBindViewHolder(@NonNull ReservationViewHolder reservationViewHolder, int position) {        TextView textViewOrderId = reservationViewHolder.textViewReservationId;        TextView textViewRemainingTime = reservationViewHolder.textViewRemainingTime;        TextView textViewCustomerId = reservationViewHolder.textViewCustomerId;        TextView textViewOrderedFood = reservationViewHolder.textViewOrderedDishes;        TextView textViewOrderNotes = reservationViewHolder.textViewReservationNotes;        ReservationModel tmpRM = dataSet.get(position);        textViewOrderId.setText("" + tmpRM.getId());        textViewRemainingTime.setText("" + tmpRM.getRemainingMinutes() + " min");        textViewCustomerId.setText("" + tmpRM.getCustomerId());        String reservationOffer = "";        for (int i = 0; i < tmpRM.getReservatedDishes().size(); i++) {            String offerName = offersData.get(tmpRM.getReservatedDishes().get(i).getDishId()).getName();            reservationOffer += offerName + "(" + tmpRM.getReservatedDishes().get(i).getDishMultiplier() + ") ";        }        textViewOrderedFood.setText(reservationOffer);        textViewOrderNotes.setText(dataSet.get(position).getNotes());    }    @Override    public int getItemCount() {        return dataSet.size();    }    public static class ReservationViewHolder extends RecyclerView.ViewHolder {        TextView textViewReservationId;        TextView textViewRemainingTime;        TextView textViewCustomerId;        TextView textViewOrderedDishes;        TextView textViewReservationNotes;        public ReservationViewHolder(View itemView) {            super(itemView);            this.textViewReservationId = itemView.findViewById(R.id.textViewOrderIdReservation);            this.textViewRemainingTime = itemView.findViewById(R.id.textViewRemainingTimeReservation);            this.textViewCustomerId = itemView.findViewById(R.id.textViewCustomerIdReservation);            this.textViewOrderedDishes = itemView.findViewById(R.id.textViewFoodReservation);            this.textViewReservationNotes = itemView.findViewById(R.id.textViewNotesReservation);        }    }}